import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Styled from 'styled-components';
import {
    SearchBox,
    RefinementListFilter,
    Hits,
    HitsStats,
    SearchkitComponent,
    SelectedFilters,
    MenuFilter,
    HierarchicalMenuFilter,
    Pagination,
    ResetFilters
    } from "searchkit";

const Button = Styled.button`
	border-radius: 3px;
	padding: 0.25em 1em;
	background: ${props => props.primary ? 'palevioletred' : 'transparent'};
	color: ${props => props.primary ? 'white' : 'palevioletred'};
	border: 2px solid palevioletred;
	font-size: ${props => props.primary ? '1em' : '1.2em'};
`;

const Input = Styled.input`
	color: palevioletred;
	font-size: 1.2em;
	border: 2px solid palevioletred;
	border-radius: 3px;
	background: transparent;
	padding: 0.25em 0.5em;
	margin: 0.5em;
`;

class Search extends Component {
	constructor(props) {
		super(props);
	}

	render() {

		return (
			<div>
    			<p className="lead">Enter company name below. </p>
    			<Input id='search_box' placeholder='Company Name' onChange={this.showSearchedOccupation}/>
				<br/>
				<Button onClick = {() => this.props.switchPage(0)}>Search</Button>
			</div>
		);
	}
}

export default Search;
