import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Styled from 'styled-components';


const Button = Styled.button`
	border-radius: 3px;
	padding: 0.25em 1em;
	background: ${props => props.primary ? 'palevioletred' : 'transparent'};
	color: ${props => props.primary ? 'white' : 'palevioletred'};
	border: 2px solid palevioletred;
	font-size: ${props => props.primary ? '1em' : '1.2em'};
`;

const Input = Styled.input`
	color: white;
	font-size: 1.2em;
	border: 2px solid palevioletred;
	border-radius: 3px;
	background: transparent;
	padding: 0.25em 0.5em;
	margin: 0.5em;
`;

class Search extends Component {
	constructor(props) {
		super(props);
		this.state = {input: ""};
		this.changeInput = this.changeInput.bind(this);
		this.showCompany = this.showCompany.bind(this);
	}

	changeInput(event) {
		if (event.target.value) {
			this.setState({input: event.target.value});
		}
	}

	showCompany(event) {
		if (this.state.input) {
			console.log(this.state.input);
		}
	}

	render() {

		return (
			<div>
    			<p className="lead">Enter company name below. </p>
    			<Input id='search_box' placeholder='Company Name' onChange = {this.changeInput}/>
				<br/>
				<Button onClick = {this.showCompany}>Search</Button>
				<br/>
				<Search_result id='search_result_container' query={this.state.input}/>
			</div>
		);
	}
}

class Search_result extends Component {
	constructor(props) {
		super(props);
		this.state = {allComps: ["OXY", "EOG", "APC", "APA", "COP", "PXD"]};
		// this.updatePossibleResults = this.updatePossibleResults.bind(this);
		// this.showDetail = this.showDetail.bind(this);
	}

	// componentDidUpdate(prevProps) {

	// 	if (this.props.query !== prevProps && this.props.query) {
	// 		console.log(1);
	// 		var currentComps = [];
	// 		for (var i = this.state.allComps.length - 1; i >= 0; i--) {
	// 			if (this.state.allComps[i].includes(this.props.query)) {
	// 				console.log(currentComps);
	// 				currentComps.push(this.state.allComps[i]);
	// 			}
	// 		}
	// 		this.setState({companies: currentComps.push(this.state.allComps[i])});
	// 	}

	// }

	// componentDidUpdate(prevProps) {
	// 	if (this.props.query !== prevProps.query) {
	// 		console.log(this.props.query);
	// 		this.state.query = this.props.query;
	// 		this.forceUpdate();
	// 	}
	// }

	render() {

		var currentComps = [];
		if (this.props.query) {
			for (var i = this.state.allComps.length - 1; i >= 0; i--) {
				if (this.state.allComps[i].includes(this.props.query)) {
					currentComps.push(this.state.allComps[i]);
				}
			}
		}

		return (
			<div>
    			{currentComps.map(company => {
    				return (<p key={company} style={{marginTop: "20px"}}>{company}</p>);
    			})}

    			<div class="container" style={{width: "100%", marginTop: "30px"}}>
			    <div class="row">
			      <div class="col-lg-4">
			        <svg class="bd-placeholder-img rounded-circle" width="140" height="140" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" aria-label="Placeholder: 140x140"><title>Placeholder</title><rect fill="#777" width="100%" height="100%"/><text fill="#777" dy=".3em" x="50%" y="50%">140x140</text></svg>
			        <h2>Heading</h2>
			        <p>Donec sed odio dui. Etiam porta sem malesuada magna mollis euismod. Nullam id dolor id nibh ultricies vehicula ut id elit. Morbi leo risus, porta ac consectetur ac, vestibulum at eros. Praesent commodo cursus magna.</p>
			        <p><a class="btn btn-secondary" href="#" role="button">View details &raquo;</a></p>
			      </div>
			      <div class="col-lg-4">
			        <svg class="bd-placeholder-img rounded-circle" width="140" height="140" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" aria-label="Placeholder: 140x140"><title>Placeholder</title><rect fill="#777" width="100%" height="100%"/><text fill="#777" dy=".3em" x="50%" y="50%">140x140</text></svg>
			        <h2>Heading</h2>
			        <p>Duis mollis, est non commodo luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit. Cras mattis consectetur purus sit amet fermentum. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh.</p>
			        <p><a class="btn btn-secondary" href="#" role="button">View details &raquo;</a></p>
			      </div>
			      <div class="col-lg-4">
			        <svg class="bd-placeholder-img rounded-circle" width="140" height="140" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" aria-label="Placeholder: 140x140"><title>Placeholder</title><rect fill="#777" width="100%" height="100%"/><text fill="#777" dy=".3em" x="50%" y="50%">140x140</text></svg>
			        <h2>Heading</h2>
			        <p>Donec sed odio dui. Cras justo odio, dapibus ac facilisis in, egestas eget quam. Vestibulum id ligula porta felis euismod semper. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus.</p>
			        <p><a class="btn btn-secondary" href="#" role="button">View details &raquo;</a></p>
			      </div>
			    </div>
			    </div>


			</div>
		);
	}
}

class CompanyChoice extends Component {
	constructor(props) {
		super(props);
	}

	render() {

		const Grid = Styled.div`
			display: grid; 
			grid-gap: 10px;
			grid-template-columns: auto auto;
			grid-template-rows: auto auto;
			margin-top: 10px;
			margin-bottom: 10px;
			border-radius: 5px;
			border: 2px solid palevioletred;
		`;
		const Detail = Styled.div`
			grid-column: ${props => props.col};
			grid-row: ${props => props.row};
			text-align: left;
			font-size: 1.5em;
		`;

		return (
			<div>
				<Grid>
					<Detail row="1" col="1">

					</Detail>
				</Grid>
			</div>
		);
	}
}






export default Search;
