import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Styled from 'styled-components';


const Button = Styled.button`
	border-radius: 3px;
	padding: 0.25em 1em;
	background: ${props => props.primary ? 'palevioletred' : 'transparent'};
	color: ${props => props.primary ? 'white' : 'palevioletred'};
	border: 2px solid palevioletred;
	font-size: ${props => props.primary ? '1em' : '1.2em'};
`;

const Input = Styled.input`
	color: white;
	font-size: 1.2em;
	border: 2px solid palevioletred;
	border-radius: 3px;
	background: transparent;
	padding: 0.25em 0.5em;
	margin: 0.5em;
`;

class Search extends Component {
	constructor(props) {
		super(props);
		this.state = {input: ""};
		this.changeInput = this.changeInput.bind(this);
		this.showCompany = this.showCompany.bind(this);
	}

	changeInput(event) {
		if (event.target.value) {
			this.setState({input: event.target.value});
		}
	}

	showCompany(event) {
		if (this.state.input) {
			console.log(this.state.input);
		}
	}

	render() {

		return (
			<div>
    			<p className="lead">Enter company name below. </p>
    			<Input id='search_box' placeholder='Company Name' onChange = {this.changeInput}/>
				<br/>
				<Button onClick = {this.showCompany}>Search</Button>
				<br/>
				<Search_result id='search_result_container' query={this.state.input}/>
			</div>
		);
	}
}

class Search_result extends Component {
	constructor(props) {
		super(props);
		this.state = {companies: [], details: {}, allComps: ["OXY", "EOG", "APC", "APA", "COP", "PXD"]};
		// this.updatePossibleResults = this.updatePossibleResults.bind(this);
		// this.showDetail = this.showDetail.bind(this);
	}

	componentDidMount() {
		if (this.props.query) {
			var currentComps = [];
			for (var i = this.state.allComps.length - 1; i >= 0; i--) {
				if (this.state.allComps[i].includes(this.props.query)) {
					console.log(currentComps);
					currentComps.push(this.state.allComps[i]);
				}
			}
			this.setState({companies: currentComps.push(this.state.allComps[i])});
		}

	}

	// componentDidUpdate(prevProps) {
	// 	if (this.props.query !== prevProps.query) {
	// 		console.log(this.props.query);
	// 		this.state.query = this.props.query;
	// 		this.forceUpdate();
	// 	}
	// }

	render() {

		return (
			<div>
    			<p className="lead">{this.props.query}</p>


			</div>
		);
	}
}






export default Search;
