import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Styled from 'styled-components';


const Button = Styled.button`
	border-radius: 3px;
	padding: 0.25em 1em;
	background: ${props => props.primary ? 'palevioletred' : 'transparent'};
	color: ${props => props.primary ? 'white' : 'palevioletred'};
	border: 2px solid palevioletred;
	font-size: ${props => props.primary ? '1em' : '1.2em'};
`;

const Input = Styled.input`
	color: white;
	font-size: 1.2em;
	border: 2px solid palevioletred;
	border-radius: 3px;
	background: transparent;
	padding: 0.25em 0.5em;
	margin: 0.5em;
`;

class Search extends Component {
	constructor(props) {
		super(props);
		this.state = {input: ""};
		this.changeInput = this.changeInput.bind(this);
		this.showCompany = this.showCompany.bind(this);
	}

	changeInput(event) {
		if (event.target.value) {
			this.setState({input: event.target.value});
		}
	}

	showCompany(event) {
		if (this.state.input) {
			console.log(this.state.input);
		}
	}

	render() {

		return (
			<div>
    			<p className="lead">Enter company name below. </p>
    			<Input id='search_box' placeholder='Company Name' onChange = {this.changeInput}/>
				<br/>
				<Button onClick = {this.showCompany}>Search</Button>
				<br/>
				<Search_result id='search_result_container' query={this.state.input}/>
			</div>
		);
	}
}

class Search_result extends Component {
	constructor(props) {
		super(props);
		this.state = {allComps: ["OXY", "EOG", "APC", "APA", "COP", "PXD"]};
		// this.updatePossibleResults = this.updatePossibleResults.bind(this);
		// this.showDetail = this.showDetail.bind(this);
	}

	// componentDidUpdate(prevProps) {

	// 	if (this.props.query !== prevProps && this.props.query) {
	// 		console.log(1);
	// 		var currentComps = [];
	// 		for (var i = this.state.allComps.length - 1; i >= 0; i--) {
	// 			if (this.state.allComps[i].includes(this.props.query)) {
	// 				console.log(currentComps);
	// 				currentComps.push(this.state.allComps[i]);
	// 			}
	// 		}
	// 		this.setState({companies: currentComps.push(this.state.allComps[i])});
	// 	}

	// }

	// componentDidUpdate(prevProps) {
	// 	if (this.props.query !== prevProps.query) {
	// 		console.log(this.props.query);
	// 		this.state.query = this.props.query;
	// 		this.forceUpdate();
	// 	}
	// }

	render() {

		var currentComps = [];
		if (this.props.query) {
			for (var i = this.state.allComps.length - 1; i >= 0; i--) {
				if (this.state.allComps[i].includes(this.props.query)) {
					currentComps.push(this.state.allComps[i]);
				}
			}
		}

		return (
			<div>
    			{currentComps.map(company => {
    				return (<p key={company} style={{marginTop: "20px"}}>{company}</p>);
    			})}

    			<div id="myCarousel" class="carousel slide" data-ride="carousel">
				    <ol class="carousel-indicators">
				      <li data-target="#myCarousel" data-slide-to="0" class="active"></li>
				      <li data-target="#myCarousel" data-slide-to="1"></li>
				      <li data-target="#myCarousel" data-slide-to="2"></li>
				    </ol>
				    <div class="carousel-inner">
				      <div class="carousel-item active">
				        <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" ><rect fill="#777" width="100%" height="100%"/></svg>
				        <div class="container">
				          <div class="carousel-caption text-left">
				            <h1>Example headline.</h1>
				            <p>Cras justo odio, dapibus ac facilisis in, egestas eget quam. Donec id elit non mi porta gravida at eget metus. Nullam id dolor id nibh ultricies vehicula ut id elit.</p>
				            <p><a class="btn btn-lg btn-primary" href="#" role="button">Sign up today</a></p>
				          </div>
				        </div>
				      </div>
				      <div class="carousel-item">
				        <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" ><rect fill="#777" width="100%" height="100%"/></svg>
				        <div class="container">
				          <div class="carousel-caption">
				            <h1>Another example headline.</h1>
				            <p>Cras justo odio, dapibus ac facilisis in, egestas eget quam. Donec id elit non mi porta gravida at eget metus. Nullam id dolor id nibh ultricies vehicula ut id elit.</p>
				            <p><a class="btn btn-lg btn-primary" href="#" role="button">Learn more</a></p>
				          </div>
				        </div>
				      </div>
				      <div class="carousel-item">
				        <svg class="bd-placeholder-img" width="100%" height="100%" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" ><rect fill="#777" width="100%" height="100%"/></svg>
				        <div class="container">
				          <div class="carousel-caption text-right">
				            <h1>One more for good measure.</h1>
				            <p>Cras justo odio, dapibus ac facilisis in, egestas eget quam. Donec id elit non mi porta gravida at eget metus. Nullam id dolor id nibh ultricies vehicula ut id elit.</p>
				            <p><a class="btn btn-lg btn-primary" href="#" role="button">Browse gallery</a></p>
				          </div>
				        </div>
				      </div>
				    </div>
				    <a class="carousel-control-prev" href="#myCarousel" role="button" data-slide="prev">
				      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
				      <span class="sr-only">Previous</span>
				    </a>
				    <a class="carousel-control-next" href="#myCarousel" role="button" data-slide="next">
				      <span class="carousel-control-next-icon" aria-hidden="true"></span>
				      <span class="sr-only">Next</span>
				    </a>
				  </div>



			</div>
		);
	}
}






export default Search;
