import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Styled from 'styled-components';
import data from '../data.json';

const Button = Styled.button`
	border-radius: 3px;
	padding: 0.25em 1em;
	background: ${props => props.primary ? 'palevioletred' : 'transparent'};
	color: ${props => props.primary ? 'white' : 'palevioletred'};
	border: 2px solid palevioletred;
	font-size: ${props => props.primary ? '1em' : '1.2em'};
`;

class Info extends Component {
	constructor(props) {
		super(props);

	}

	render() {
		if (this.props.ticker) {
			const myData = data[this.props.ticker];

			const Grid = Styled.div`
				display: grid; 
				grid-template-columns: auto auto;
				grid-template-rows: auto auto auto auto auto;
				border-radius: 10px;
				border: 2px solid palevioletred;
			`;
			const Detail = Styled.div`
				grid-column: ${props => props.col};
				grid-row: ${props => props.row};
				text-align: center;
				font-size: 1.5em;
				padding: 5px;
				border-bottom: ${props => props.row == 5 ? 'none' : '2px solid palevioletred'};
			`;

			const Name = Styled.h3`
				color: white;
				margin-top: 10px;
			`;

			return (
				<div>
					<h1 className="cover-heading">Company Insights for {this.props.ticker}</h1>
					<Grid>
						<Detail row="1" col="1">
							Ticker
						</Detail>
						<Detail row="1" col="2">
							{myData["ticker"]}
						</Detail>
						<Detail row="2" col="1">
							Stock Price
						</Detail>
						<Detail row="2" col="2">
							{myData["stock_price"]}
						</Detail>
						<Detail row="3" col="1">
							Trend
						</Detail>
						<Detail row="3" col="2">
							{myData["trend"]}
						</Detail>
						<Detail row="4" col="1">
							Articles
						</Detail>
						<Detail row="4" col="2">
							<ul>
							{myData["articles"].map(article => {
								return (<li>{article}</li>);
							})}
							</ul>
						</Detail>
					</Grid>
				</div>
			);
		} else {
			return (<div></div>);
		}

	}
}

export default Info;
